package Practice;

public class POC2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1;i<=4;i++) {
			for(int j=1;j<=4;j++) {
				if(i>=j) {
					System.out.print("1");
				}
				else
				{
					System.out.print("");
				}
			}
			System.out.println(" ");
		}
		
	}

}
